mapper.select:
select rnum, no, title, writer, writeDate
from(
    select rownum rnum, no, title, writer, writeDate
    from (
    select no, title, writer, writeDate
    from tbl_board
    order by no desc
    )
    )
where rnum between 1 and 10

select no, title, content, writer, writeDate, hit
from tbl_board
where no = #{no}

mapper.update:
update tbl_board
set title = #{title}, content = #{content}, writer = #{writer}
where no = #{no}
